import { combineReducers } from 'redux'

let defaultState = {
  stb_RollTotal: null,
  stb_gameDiceRolls: {
    one: 0,
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0
    },
  stb_gameRollSums: {
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0,
    seven: 0,
    eight: 0,
    nine: 0,
    ten: 0,
    eleven: 0,
    twelve: 0
    },
  stb_userDiceRolls: {
    one: 0,
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0
    },
  stb_userRollSums: {
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0,
    seven: 0,
    eight: 0,
    nine: 0,
    ten: 0,
    eleven: 0,
    twelve: 0
    },
  stb_allDiceRolls: {
    one: 0,
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0
    },
  stb_allRollSums: {
    two: 0,
    three: 0,
    four: 0,
    five: 0,
    six: 0,
    seven: 0,
    eight: 0,
    nine: 0,
    ten: 0,
    eleven: 0,
    twelve: 0
  },

}


function stb_RollTotalReducer(state = defaultState.stb_RollTotal, action) {
  switch (action.type) {
      case "SET ROLL SUM":
          return action.payload
      default:
          return state
  }
}



function stb_gameDiceRollsReducer(state = { ...defaultState.stb_gameDiceRolls }, action) {
  switch (action.type) {
      case "ADD ROLL":
          return action.payload
      default:
          return state
  }
} // ends stb_gameDiceRollsReducer

function stb_gameRollSumsReducer(state = { ...defaultState.stb_gameRollSums }, action) {
  switch (action.type) {
      case "ADD SUM":
          return action.payload
      default:
          return state
  }
} // ends stb_gameRollSumsReducer

function stb_userDiceRollsReducer(state = { ...defaultState.stb_userDiceRolls }, action) {
  switch (action.type) {
      case "SOMETHING":
          return action.payload
      default:
          return state
  }
} // ends stb_userDiceRollsReducer

function stb_userRollSumsReducer(state = { ...defaultState.stb_userRollSums }, action) {
  switch (action.type) {
      case "ADD SUM":
          return action.payload
      default:
          return state
  }
} // ends stb_userRollSumsReducer

function stb_allDiceRollsReducer(state = { ...defaultState.stb_allDiceRolls }, ...action) {
  switch (action.type) {
      case "SOMETHING":
          return action.payload
      default:
          return state
  }
} // ends stb_allDiceRollsReducer

function stb_allRollSumsReducer(state = { ...defaultState.stb_allRollSums }, action) {
  switch (action.type) {
      case "ADD SUM":
          return action.payload
      default:
          return state
  }
} // ends stb_allRollSumsReducer





let reducer = combineReducers({
  stb_RollTotal: stb_RollTotalReducer,
  stb_gameDiceRolls: stb_gameDiceRollsReducer,
  stb_gameRollSums: stb_gameRollSumsReducer,
  stb_userDiceRolls: stb_userDiceRollsReducer,
  stb_userRollSums: stb_userRollSumsReducer,
  stb_allDiceRolls: stb_allDiceRollsReducer,
  stb_allRollSums: stb_allRollSumsReducer,
})

export default reducer
