import React from 'react'
// import { connect } from 'react-redux'
// import { setSelectedCampaignAndContributions } from '../actions'


class CreateNumberTile extends React.Component {

  state = {
    flipped: false,
  }

  flipTile = () => {
    this.setState({
      flipped: true
    })
  }


  render(){

    switch (this.props.cardType) {
      case "TopCampaigns":
        return (
          <div className="CampaignCard" onClick={null}> 
          <h2>{this.props.campaignObj.campaign_name}</h2>
            <img className="campaignCard" alt="Card" src={this.props.campaignObj.card_image}  />
            <p>Status: { mapStatusIdToStatusName(this.props.campaignObj.campaign_status_id)}</p>
            {/* <button onClick={setSelectedCampaignAndContributions("campaigns/" + this.props.campaignObj.id, this.props.campaignObj, this.props.history)} >View Details</button> */}
            {/* <div id="CampaignCardDescription" >
              <h5>
                {this.props.campaignObj.campaign_description}
              </h5>
            </div> */}
            <button id="campaignCardButton" onClick={() => this.props.setSelectedCampaignAndContributions(this.props.campaignObj, this.props.history)} >View Details</button>

          </div>
          )// ends return

      default:  return "HI"

    } // ends switch/case
  }// ends render
} // ends class

